const data = $input.first().json;

function escapeMarkdown(text) {
  if (!text) return '';
  return text.replace(/[_*\[\]()~`>#+=|{}.!-]/g, '\\$&');
}

function cleanText(text) {
  if (!text) return '';
  return text.replace(/<[^>]*>/g, '').trim();
}

// Parse events (top 5) - only keep essential data
const events = (data.events || []).slice(0, 5).map(event => {
  const page = event.pages?.[0];
  return {
    year: event.year,
    text: cleanText(event.text),
    url: page?.content_urls?.desktop?.page || null
  };
});

// Parse births (top 3) - only keep essential data
const births = (data.births || []).slice(0, 3).map(birth => {
  const page = birth.pages?.[0];
  return {
    year: birth.year,
    text: cleanText(birth.text),
    url: page?.content_urls?.desktop?.page || null
  };
});

// Parse deaths (top 3) - only keep essential data
const deaths = (data.deaths || []).slice(0, 3).map(death => {
  const page = death.pages?.[0];
  return {
    year: death.year,
    text: cleanText(death.text),
    url: page?.content_urls?.desktop?.page || null
  };
});

